programlisting:
/// 
/// Creates a new IAM group.
/// 
/// The IAM Client object.
/// The string representing the name for the
/// new group.
/// Returns the response object returned by CreateGroupAsync.
public static async Task CreateNewGroupAsync(
    AmazonIdentityManagementServiceClient client,
    string groupName)
{
    var createGroupRequest = new CreateGroupRequest
    {
        GroupName = groupName,
    };

    Console.WriteLine("--------------------------------------------------------------------------------------------------------------");
    Console.WriteLine("Start by creating the group...");
    var response = await client.CreateGroupAsync(createGroupRequest);
    Console.WriteLine($"Successfully created the group: {response.Group.GroupName}");
    Console.WriteLine("--------------------------------------------------------------------------------------------------------------\n");

    return response;
}
