programlisting:
/// 
/// Creates a new access key for the user represented by the userName
/// parameter.
/// 
/// The client object which will call
/// CreateAccessKeyAsync.
/// The name of the user for whom an access key
/// is created by the call to CreateAccessKeyAsync.
/// Returns the response from the call to
/// CreateAccessKeyAsync.
public static async Task CreateNewAccessKeyAsync(
    AmazonIdentityManagementServiceClient client,
    string userName)
{
    try
    {
        // Create an access key for the IAM user that can be used by the SDK.
        var response = await client.CreateAccessKeyAsync(new CreateAccessKeyRequest
        {
            // Use the user you created in the CreateUser example
            UserName = userName,
        });
        return response;
    }
    catch (LimitExceededException e)
    {
        Console.WriteLine(e.Message);
        return null;
    }
}
