public static void scan( DynamoDbEnhancedClient enhancedClient) {

    try{
        // Create a DynamoDbTable object
        DynamoDbTable custTable = enhancedClient.table("Customer", TableSchema.fromBean(Customer.class));
        Iterator results = custTable.scan().items().iterator();
        while (results.hasNext()) {

            Customer rec = results.next();
            System.out.println("The record id is "+rec.getId());
            System.out.println("The name is " +rec.getCustName());
        }

    } catch (DynamoDbException e) {
        System.err.println(e.getMessage());
        System.exit(1);
    }
    System.out.println("Done");
}
