programlisting:
async fn show_state(client: &Client, id: &str) -> Result<(), Error> { let resp = client .describe_snapshots() .filters(Filter::builder().name("snapshot-id").values(id).build()) .send() .await?;
println!( "State: {}", resp.snapshots() .unwrap() .first() .unwrap() .state() .unwrap() .as_ref() );
Ok(()) }
