zipFile := flag.String("z", "", "The name of the ZIP file, without the .zip extension.") bucket := flag.String("b", "", "the name of bucket to which the ZIP file is uploaded.") function := flag.String("f", "", "The name of the Lambda function.") handler := flag.String("h", "main", "The name of the package.class handling the call.") roleARN := flag.String("a", "", "The ARN of the role that calls the function.") runtime := flag.String("r", "go1.x", "The runtime for the function.")
flag.Parse()
if *zipFile == "" || *bucket == "" || *function == "" || *handler == "" || *roleARN == "" || *runtime == "" { fmt.Println("You must supply a zip file name, bucket name, function name, handler (package) name, role ARN, and runtime value.") return }
