public static List listBrokers(MqClient mqClient) {
    List brokerList = new ArrayList<>();
    try {
        ListBrokersResponse response = mqClient.listBrokers();
        brokerList = response.brokerSummaries();

        return brokerList;

    } catch (MqException e) {
        System.err.println(e.awsErrorDetails().errorMessage());
        System.exit(1);
    }
    return brokerList;
}
