// Create a PollyClient
$client = new Aws\Polly\PollyClient([
    'profile' => 'default',
    'version' => '2016-06-10',
    'region' => 'us-east-2'
]);

$taskId = 'ab01cd23-ef45-67gh-ij89-ab01cd23ef45';

try {
    $result = $client->getSpeechSynthesisTask([
        'TaskId' => $taskId,
    ]);
    var_dump($result);
} catch (AwsException $e) {
    // output error message if fails
    echo $e->getMessage();
    echo "\n";
}
