$s3Client = new S3Client([
    'region' => 'us-west-2',
    'version' => '2006-03-01'
]);

//Delete all Objects when versioning is not enabled
try {
    $objects = $s3Client->getIterator('ListObjects', ([
        'Bucket' => $BUCKET_NAME
    ]));
    echo "Keys retrieved!\n";
    foreach ($objects as $object) {
        echo $object['Key'] . "\n";
        $result = $s3Client->deleteObject([
            'Bucket' => $BUCKET_NAME,
            'Key' => $object['Key'],
        ]);
    }
    $result = $s3Client->deleteBucket([
        'Bucket' => $BUCKET_NAME,
    ]);
} catch (S3Exception $e) {
    echo $e->getMessage() . "\n";
}

//Delete bucket and all versioned objects inside bucket when versioning is enabled.
try {
    $versions = $s3Client->listObjectVersions([
        'Bucket' => $BUCKET_NAME
    ])->getPath('Versions');
    echo "Keys retrieved!\n";
    foreach ($versions as $version) {
        echo $version['Key'] . "\n";
        echo $version['VersionId'] . "\n";
        $result = $s3Client->deleteObject([
            'Bucket' => $bucket,
            'Key' => $version['Key'],
            'VersionId' => $version['VersionId']
        ]);
    }
    $result = $s3Client->deleteBucket([
        'Bucket' => $BUCKET_NAME,
    ]);
} catch (S3Exception $e) {
    echo $e->getMessage() . "\n";
}
