programlisting:
$USAGE = "\n" .
    "To run this example, supply the name of an S3 bucket and object\n" .
    "name (key) to delete.\n" .
    "\n" .
    "Ex: php DeleteObject.php  \n";

if (count($argv) <= 2) {
    echo $USAGE;
    exit();
}

$bucket = $argv[1];
$key = $argv[2];

try {
    //Create a S3Client
    $s3Client = new S3Client([
        'region' => 'us-west-2',
        'version' => '2006-03-01'
    ]);
    $result = $s3Client->deleteObject([
        'Bucket' => $bucket,
        'Key' => $key,
    ]);
} catch (S3Exception $e) {
    echo $e->getMessage() . "\n";
}
