programlisting:
$client = new SecretsManagerClient([
    'profile' => 'default',
    'version' => '2017-10-17',
    'region' => 'us-west-2'
]);
$secretName = '<<{{MySecretName}}>>';

try {
    $result = $client->listSecretVersionIds([
        'SecretId' => $secretName,
    ]);
    var_dump($result);
}  catch (AwsException $e) {
    // output error message if fails
    echo $e->getMessage();
    echo "\n";
}
